/**
 * Set environments of the config-server in the table idcdb10dev - Insert properties
 * @module clusters_properties
 * @author Victor David Avila Varela - victoravila@soyyo.co
 * @copyright 2019
 */

-- --------------------------------------------------------------------
-- STAGE
SET @STAGE = 'DEV';

-- TRACING
SET @TRACING_LEVEL = 'DEBUG';

-- GATEWAY
SET @ZUUL_PORT='8762';
SET @ZUUL_HOST='gateway-dev.idcolombia.co';

-- EUREKA
SET @EUREKA_HOST = 'localhost';
SET @EUREKA_PORT = '8761';

-- PORTS SERVICES
SET @PORT_PARAMETER = '6131';

-- HOST SERVICES
SET @HOST = 'localhost';

-- DB CONECTION
-- DB NAME PARA PARAMETROS --
SET @DB_NAME_ES_PARAMETER = 'soyyo03dev';

-- Conexión a Secret Manager
SET @AWS_REGION = 'us-east-1';
SET @SECRET_MANAGER_DB = 'DATABASE_TEST_20210302';


-- ----------------------------------------------------------------
-- D03.ES.Parameter
-- -----------------------------------------------------------------
-- ---------Delete de los registros por aplication-------------------
DELETE FROM properties WHERE APPLICATION='D03.ES.Parameter' ;

-- ---------Insert de los registros por aplication-------------------
INSERT INTO properties (APPLICATION,PROFILE,LABEL,`KEY`,VALUE) VALUES
('D03.ES.Parameter',@STAGE,'latest','HOST',@HOST)
,('D03.ES.Parameter',@STAGE,'latest','PORT',@PORT_PARAMETER)
,('D03.ES.Parameter',@STAGE,'latest','PREFIX_PATH','/services/auth/1.0.0/es_parameter/')
,('D03.ES.Parameter',@STAGE,'latest','EUREKA_ID','D03.ES.Parameter')
,('D03.ES.Parameter',@STAGE,'latest','EUREKA_HOST',@EUREKA_HOST)
,('D03.ES.Parameter',@STAGE,'latest','EUREKA_PORT',@EUREKA_PORT)
,('D03.ES.Parameter',@STAGE,'latest','DB_NAME_ES_PARAMETER',@DB_NAME_ES_PARAMETER)
,('D03.ES.Parameter',@STAGE,'latest','SECRET_MANAGER_DB',@SECRET_MANAGER_DB)
,('D03.ES.Parameter',@STAGE,'latest','AWS_REGION',@AWS_REGION)
,('D03.ES.Parameter',@STAGE,'latest','TRACING_LEVEL',@TRACING_LEVEL)
;
COMMIT;
